import React, { useState, useEffect } from 'react'
import axios from 'axios'
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper'
import { Avatar, Typography, Box } from '@material-ui/core'
import { withStyles } from '@material-ui/styles'

function CoinPrices() {
	const [coinPrices, setCoinPrices] = useState([])

	const TableHead = withStyles(theme => ({
		root: {
			backgroundColor: 'orange'
		}
	}))(TableHead);

	const TableHeaderCell = withStyles(theme => ({
		root: {
			color: 'white'
		}
	}))(TableCell);

	useEffect(() => {
		axios
			.get('https://api.coinstats.app/public/v1/coins?skip=0&limit=5')
			.then(response => {
				setCoinPrices(response.data.coins)
			})
	}, [])

	return (
		<div>
			<h3>Coinbases: Coin Price</h3>
			<TableContainer component={Paper}>
				<Table>
					<TableHead>
						<TableRow>
							<TableHeaderCell>Coin Name</TableHeaderCell>
							<TableHeaderCell>Current Price</TableHeaderCell>
						</TableRow>
					</TableHead>
					<TableBody>
						{coinPrices.map((item) => (
							<TableRow>
								<TableCell>
									{item.name}
								</TableCell>
								<TableCell>
									${item.price.toFixed(2)}
								</TableCell>
							</TableRow>
						))}
					</TableBody>
				</Table>
			</TableContainer>
		</div>
	)
}

export default CoinPrices
