import React, { useState, useEffect } from 'react'
import axios from 'axios'
import WbSunnyIcon from '@mui/icons-material/WbSunny';
import Card from '@mui/material/Card'
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import { Avatar, Typography, Box } from '@material-ui/core'
import { makeStyles } from '@material-ui/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    textAlign: 'center',
  },
}));


function Weather() {
	const [weather, setWeather] = useState([])
	const classes = useStyles()

	useEffect(() => {
		axios
			.get('https://api.weatherapi.com/v1/current.json?key=1fb6df188b0143648c5223702212409&q=belmont ma&aqi=yes')
			.then(response => {
				setWeather(response.data.current)
				// console.log(response.data.current)
			})
	}, [])

	return (
		<div>
			<Card className={classes.root}>
				<CardContent>
					<Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
						Belmont, MA
					</Typography>
					<Box sx={{ display: 'flex'}}>
						{/* <Avatar src={weather.condition.icon}></Avatar> */}
						<Typography variant="h3" component="div" sx={{ ml: 2}}>
							{ weather.temp_c }Â°c
						</Typography>
					</Box>
					<Typography variant="body2" color="text.secondary">
						{ weather.condition.text }
					</Typography>
					<Box sx={{ display: 'flex', mt: 1}}>
						<Typography variant="body2" component="div">
							Wind MPH: { weather.wind_mph }
						</Typography>
						<Typography variant="body2" component="div" sx={{ ml: 2}}>
							Humidty: { weather.humidity }%
						</Typography>
					</Box>
				</CardContent>
			</Card>
		</div>
	)
}

export default Weather

